		double x = 1.2340;
		double y = 10.3459;
		
		System.out.print("전체 7자리로 맞추고 소수 4자리까지 출력\n");
		System.out.printf("x = %7.4f\n",x);
		System.out.printf("y = %7.4f\n\n",y);
		
